mapper.select:
SELECT 

FROM c_balance a

WHERE a.id = #{id}

SELECT 

FROM c_balance a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.no LIKE 
			'%'||#{no}||'%'
			'%'+#{no}+'%'
			concat('%',#{no},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_balance a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_balance SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
