mapper.select:
SELECT 

FROM c_balance_sale a

WHERE a.id = #{id}

SELECT
FROM c_balance_sale a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.sale_id LIKE '%'||#{saleId}||'%' '%'+#{saleId}+'%' concat('%',#{saleId},'%')
AND a.sale_no LIKE '%'||#{saleNo}||'%' '%'+#{saleNo}+'%' concat('%',#{saleNo},'%')
AND a.balance_no LIKE '%'||#{balanceNo}||'%' '%'+#{balanceNo}+'%' concat('%',#{balanceNo},'%')
AND a.sale_time BETWEEN #{beginSaleTime} AND #{endSaleTime}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_balance_sale a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_balance_sale SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
