SELECT
FROM j_bill_order a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.no LIKE '%'||#{no}||'%' '%'+#{no}+'%' concat('%',#{no},'%')
AND stockOrder.no LIKE '%'||#{stockOrder.no}||'%' '%'+#{stockOrder.no}+'%' concat('%',#{stockOrder.no},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM j_bill_order a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
