mapper.select:
SELECT 

FROM c_oper_order_pay a

WHERE a.id = #{id}

SELECT 

FROM c_oper_order_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.oper_order_id = #{operOrder.id}


		AND (a.no LIKE 
			'%'||#{no}||'%'
			'%'+#{no}+'%'
			concat('%',#{no},'%')
		OR b.no LIKE 
			'%'||#{no}||'%'
			'%'+#{no}+'%'
			concat('%',#{no},'%')
			)


		AND a.create_date BETWEEN #{startDate} AND #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT
FROM c_oper_order_pay a LEFT JOIN c_oper_order b on a.oper_order_id = b.id LEFT JOIN c_store c on c.id = b.store_id
a.del_flag = '0' AND b.store_id = #{storeId} AND a.create_date BETWEEN #{startDate} AND #{endDate}

SELECT 

FROM c_oper_order_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM c_oper_order_pay


		WHERE id = #{id}


		WHERE oper_order_id = #{operOrder.id}

UPDATE c_oper_order_pay SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE oper_order_id = #{operOrder.id}
