mapper.select:
SELECT 

FROM c_product a

WHERE a.id = #{id}

SELECT 

FROM c_product a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.agency_id = #{agency.id}


		AND (a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')
		OR a.brevity_code LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')
			)

	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE c_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
