mapper.select:
SELECT 

FROM j_purchase_order_detail a

WHERE a.id = #{id}

SELECT 

FROM j_purchase_order_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.order_no = #{purchaseOrder.no}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM j_purchase_order_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM j_purchase_order_detail


		WHERE id = #{id}


		WHERE order_no = #{purchaseOrder.no}

UPDATE j_purchase_order_detail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_no = #{purchaseOrder.no}
