mapper.select:
SELECT 

FROM c_report a

WHERE a.id = #{id}

SELECT 

FROM c_report a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.date LIKE 
			'%'||#{date}||'%'
			'%'+#{date}+'%'
			concat('%',#{date},'%')


		AND a.store_id = #{store.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_report a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_report SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
