mapper.select:
SELECT 

FROM j_return_bill a

WHERE a.id = #{id}

SELECT
FROM j_return_bill a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.no LIKE '%'||#{no}||'%' '%'+#{no}+'%' concat('%',#{no},'%')
AND returnOrder.no LIKE '%'||#{returnOrder.no}||'%' '%'+#{returnOrder.no}+'%' concat('%',#{returnOrder.no},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM j_return_bill a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
