mapper.update:
DELETE FROM j_stock_order_detail


		WHERE id = #{id}


		WHERE order_no = #{stockOrder.no}

UPDATE j_stock_order_detail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_no = #{stockOrder.no}
