mapper.select:
SELECT 

FROM j_warehouse a

WHERE a.id = #{id}

SELECT
FROM j_warehouse a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND (product.name LIKE '%'||#{product.name}||'%' '%'+#{product.name}+'%' concat('%',#{product.name},'%') OR product.brevity_code LIKE '%'||#{product.name}||'%' '%'+#{product.name}+'%' concat('%',#{product.name},'%') )
AND a.is_retail = #{isRetail}
AND a.expiry_date BETWEEN #{beginExpiryDate} AND #{endExpiryDate}
AND a.company LIKE '%'||#{company}||'%' '%'+#{company}+'%' concat('%',#{company},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM j_warehouse a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE j_warehouse SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COALESCE(SUM(w.quantity), 0) as sum FROM j_warehouse w WHERE w.product_id = #{id}
 and w.is_retail = ${isRetail} 
 and w.company = '${company}' 
 and del_flag = 0;
