mapper.select:
SELECT 

FROM sys_schedule a

WHERE a.id = #{id}

SELECT 

FROM sys_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name = #{name}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_schedule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
