mapper.select:
SELECT 

FROM sys_datarule a

WHERE a.id = #{id}

SELECT 

FROM sys_datarule a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.menu_id =#{menuId}




		ORDER BY ${page.orderBy}

SELECT 

FROM sys_datarule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_datarule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
