mapper.select:
SELECT 

FROM test_car a

WHERE a.id = #{id}

		SELECT 

		FROM test_car a


			a.del_flag = #{DEL_FLAG_NORMAL}
			${dataScope}

                        AND (b.id = #{kind.id} OR b.parent_ids LIKE
                        '%,'||#{kind.id}||',%')
                        CONCAT('%,', #{kind.id}, ',%'))
                        '%'+#{kind.id}+'%')




				ORDER BY ${page.orderBy}


				ORDER BY a.create_date ASC

SELECT 

FROM test_car a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM test_car


		WHERE id = #{id}


		WHERE kind_id = #{kind.id}

UPDATE test_car SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE kind_id = #{kind.id}
